                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="Name" />
        </Form.Group>
        <Form.Group controlId="formBasicEmail" className="col col-sm-6">
            <Form.Label>Last Name</Form.Label>
            <Form.Control type="text"
                className="input"
                value={age}
                onChange={(e) => setAge(e.target.value)}
                placeholder="Age" />
        </Form.Group>
    </Row>
    </form>





  );
};

export default AddUser;
